def color(x, y, k):    #La fonction retourne pour chaque pixel la couleur à attribuer.
    return 0.5 < ((y // 17) // (2 ** (17 * x + (y % 17)))) % 2


def draw(k):
    x_range = np.arange(0, 106)
    y_range = np.arange(0, 17)
    X, Y = np.meshgrid(x_range, y_range)
    Z = color(X, Y + k, k)
    Z = Z[::-1, :]

    plt.figure(figsize=(10, 6))
    plt.imshow(Z, cmap="Greys", interpolation="nearest")
    plt.axis("off")
    plt.show()


draw(k)



